package Practical;

public class Positive_NegativeNumber {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double number=100;
		
		
      if(number<0.0)
      {
    	  System.out.println(number + " is negative number");
    	  
      }
      else if(number>0.0){
    	  System.out.println(number + " is postive number");
      }
      else
      {
    	  System.out.println(number + " is zero number");
      }
	}

}
