package Practical;

public class QuotientAndReminder {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int dividend = 350;
		int divisor= 5;
		int remindar = dividend%divisor;
		int Quotient = dividend/divisor;
		
		System.out.println("Remindar is " +remindar);
		System.out.println("Remindar is " +Quotient);
		
		
	}

}
